package p2_9;

public class Replace {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		rep("mi55i5ipi");
	}

	private static void rep(String bean) {
		

			bean=bean.replace('i', '!');
		
			bean=bean.replace('5', '$');
			System.out.println(bean);
	
	}}


